// src/components/PerformanceChart.js
import React, { useState, useEffect} from 'react';
import {Radar, RadarChart, PolarGrid, PolarAngleAxis, PolarRadiusAxis, ResponsiveContainer} from 'recharts';
import { getUserPerformance } from '../services/apiService';

// Mapping des labels en français
const labelsFrench = {
  cardio: 'Cardio',
  energy: 'Énergie',
  endurance: 'Endurance',
  strength: 'Force',
  speed: 'Vitesse',
  intensity: 'Intensité'
};

const PerformanceChart = ({ userId }) => {
  const [performanceData, setPerformanceData] = useState([]);

  useEffect(() => {
    const fetchPerformanceData = async () => {
      const { data, kind } = await getUserPerformance(userId);
      if (data && kind) {
        const formattedData = data.map(item => ({
          subject: labelsFrench[kind[item.kind]], // Utilisation du mapping pour convertir en français
          A: item.value,
          fullMark: 200
        }));
        setPerformanceData(formattedData);
      }
    };

    fetchPerformanceData();
  }, [userId]);

  return (
    <div className='performance-chart-bloc'>
      <ResponsiveContainer className={'performance-charts-container'} width="100%" height="100%">
        <RadarChart cx="50%" cy="50%" outerRadius="70%" data={performanceData}>
          <PolarGrid />
          <PolarAngleAxis dataKey="subject" tick={{ fill: '#FFFFFF', fontSize: "0.61vw"}} />
          <PolarRadiusAxis angle={30} domain={[0, 200]} tick={false} />
          <Radar name="Performance" dataKey="A" stroke="#E60000" fill="#E60000" fillOpacity={0.7} />
        </RadarChart>
      </ResponsiveContainer>
    </div>
  );
};

export default PerformanceChart;
