  return (
    <div className="App">
      <UserProfile userId="12" />
      <DailyActivityChart data={userActivity} />
      <AverageSessionsChart data={userAverageSessions} />
      <PerformanceChart data={userPerformance} />
      <ScoreChart score={score} />
    </div>
  );
};

export default App;
