import React from 'react';

// Import des icônes - ajustez le chemin selon votre structure de dossiers
import caloriesIcon from '../public/icons/calories-icon.png';
import proteinIcon from '../public/icons/protein-icon.png';
import carbsIcon from '../public/icons/carbs-icon.png';
import fatIcon from '../public/icons/fat-icon.png';

// Composant pour les informations clés
const KeyData = ({ data }) => {
  // Destructurer les données clés pour un accès facile
  const { calorieCount, proteinCount, carbohydrateCount, lipidCount } = data.keyData;

  return (
    <div className="key-data-container">
      <div className="key-data-item">
        <img src={caloriesIcon} alt="Calories" />
        <p>{calorieCount}kCal</p>
        <p>Calories</p>
      </div>
      <div className="key-data-item">
        <img src={proteinIcon} alt="Proteins" />
        <p>{proteinCount}g</p>
        <p>Proteines</p>
      </div>
      <div className="key-data-item">
        <img src={carbsIcon} alt="Carbs" />
        <p>{carbohydrateCount}g</p>
        <p>Glucides</p>
      </div>
      <div className="key-data-item">
        <img src={fatIcon} alt="Fat" />
        <p>{lipidCount}g</p>
        <p>Lipides</p>
      </div>
    </div>
  );
};

export default KeyData;
