// src/components/ScorePieChart.js
import React, { useState, useEffect } from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer} from 'recharts';
import { getUserData } from '../services/apiService';

// Component affiche un graphique circulaire représentant le score d'un utilisateur
const ScorePieChart = ({ userId }) => {
  const [userScore, setUserScore] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const { data } = await getUserData(userId);
        // Extraction de 'todayScore' ou 'score' de l'objet 'data'
        const score = data.todayScore || data.score;
        setUserScore(score);
      } catch (error) {
        console.error("Erreur lors de la récupération du score de l'utilisateur", error);
      }
    };

    fetchData();
  }, [userId]);

  if (userScore === null) {
    return <div>Chargement du score...</div>;
  }

  const scorePercentage = userScore * 100;
  const chartData = [
    { name: 'Score', value: scorePercentage, fill: '#FF0000' },
    { name: 'Rest', value: 100 - scorePercentage, fill: 'transparent' },
  ];

  // Afficher un message de chargement tant que les données ne sont pas chargées
  if (userScore === null) {
    return <div>Chargement du score...</div>;
  }

  return (
    <div className='score-pie-container'>
      <ResponsiveContainer className={'score-pie-contain'} width="100%" height={300}>
        <h2 className='score-pie-title'>
          Score
        </h2>
        <PieChart>
          <Pie
              data={chartData}
              cx="50%"
              cy="50%"
              startAngle={90}
              endAngle={270} // Inverser le sens de remplissage
              innerRadius="60%"
              outerRadius="80%"
              paddingAngle={5}
              dataKey="value"
              cornerRadius={10}
              blendStroke
          >
          {chartData.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={entry.fill} />
              ))}
          </Pie>
              {/* Utilisation de deux éléments Text pour le retour à la ligne */}
              <text x="50%" y="55%" textAnchor="middle" dominantBaseline="central" className="chart-number">
                  {`${scorePercentage}%`}
              </text>
              <text x="50%" y="60%" textAnchor="middle" dominantBaseline="central" className="chart-label">
                  de votre
              </text>
              <text x="50%" y="65%" textAnchor="middle" dominantBaseline="central" className="chart-label">
                  objectif
              </text>
        </PieChart>
      </ResponsiveContainer>
    </div>
  );
};

export default ScorePieChart;
