// src/components/PerformanceChart.js
import React, { useState, useEffect} from 'react';
import {Radar, RadarChart, PolarGrid, PolarAngleAxis, PolarRadiusAxis, ResponsiveContainer} from 'recharts';
import { getUserPerformance } from '../services/apiService';

const PerformanceChart = ({ userId }) => {
  const [performanceData, setPerformanceData] = useState([]);

  useEffect(() => {
    const fetchPerformanceData = async () => {
      const response = await getUserPerformance(userId);
      if (response && response.data && response.kind) {
        // Transformer les données ici pour les adapter à l'attente du RadarChart
        const formattedData = response.data.map(item => ({
          subject: response.kind[item.kind], // Utiliser le mapping 'kind' pour obtenir le nom du type de performance
          A: item.value,
          fullMark: 200 // Ajustez cette valeur si nécessaire
        }));
        setPerformanceData(formattedData);
      }
    };

    fetchPerformanceData();
  }, [userId]);

  return (
    <ResponsiveContainer width="100%" height={300}>
      <RadarChart cx="50%" cy="50%" outerRadius="80%" data={performanceData}>
        <PolarGrid />
        <PolarAngleAxis dataKey="subject" />
        <PolarRadiusAxis angle={30} domain={[0, 'dataMax']} />
        <Radar name="Performance" dataKey="A" stroke="#8884d8" fill="#8884d8" fillOpacity={0.6} />
      </RadarChart>
    </ResponsiveContainer>
  );
};

export default PerformanceChart;
