import React from 'react';
import { DataProvider, useData } from './DataContext';
import UserProfile from './components/UserProfile';
import DailyActivityChart from './components/DailyActivityChart';
import AverageSessionsChart from './components/AverageSessionsChart';
import PerformanceChart from './components/PerformanceChart';
import ScorePieChart from './components/ScorePieChart';
import KeyData from './components/KeyData';
import './App.css';
import Sidebar from './components/Sidebar';
import Topbar from './components/Topbar';

// Composant pour l'interface utilisateur qui utilise le contexte de données
const UserInterface = () => {
    const { toggleUserId, toggleDataSrc, useMockData } = useData();

    return (
        <div className="App">
            <Topbar />
            <Sidebar />
            <button className='switch-id-button' onClick={toggleUserId}>Changer d'utilisateur</button>
            <button className='toggle-data-src-button' onClick={toggleDataSrc}>
                {useMockData ? 'Utiliser les données du serveur' : 'Utiliser les données mockées'}
            </button>
            <div className='user-profil-contain'>
                <UserProfile />
            </div>
            <div className='main-tool'>
                <div className='main-graph'>
                    <DailyActivityChart />
                    <div className='litle-graph'>
                        <AverageSessionsChart />
                        <PerformanceChart />
                        <ScorePieChart />
                    </div>
                </div>
                <div className='main-data'>
                    <KeyData />
                </div>
            </div>
        </div>
    );
};

// Composant racine de l'application qui intègre le fournisseur de contexte
const App = () => {
    return (
        <DataProvider>
            <UserInterface />
        </DataProvider>
    );
};

export default App;
