// CustomTooltip.js
import React from 'react';

const CustomTooltip = ({ active, payload, label, type }) => {
  if (active && payload && payload.length) {
    return (
      <div className="custom-tooltip" style={{ backgroundColor: '#E60000', color:'#FFF', fontSize:'14px', padding: '10px', border: '1px solid #E60000', borderRadius: '5px' }}>
        {type === 'dailyActivity' && (
          <>
            <p className="label"></p>
            <p>{`${payload[0].value}kg`}</p>
            <p>{`${payload[1].value}kcal`}</p>
          </>
        )}
        {type === 'averageSessions' && (
          <>
            <p className="label"></p>
            <p>{`Durée: ${payload[0].value}min`}</p>
          </>
        )}
      </div>
    );
  }
  return null;
};

export default CustomTooltip;
