import React, { useState, useEffect } from 'react';
import UserProfile from './components/UserProfile';
import DailyActivityChart from './components/DailyActivityChart';
import AverageSessionsChart from './components/AverageSessionsChart';
import PerformanceChart from './components/PerformanceChart';
import ScorePieChart from './components/ScorePieChart';
import { getUserData, getUserActivity, getUserAverageSessions, getUserPerformance } from './services/apiService';
import KeyData from './components/KeyData';
import './App.css';
import Sidebar from './components/Sidebar';
import Topbar from './components/Topbar';

const App = () => {
  const userId = 12; // Exemple d'ID utilisateur statique, vous pourriez le rendre dynamique selon votre application
  const [userData, setUserData] = useState({});
  const [userActivity, setUserActivity] = useState({});
  const [userAverageSessions, setUserAverageSessions] = useState({});
  const [userPerformance, setUserPerformance] = useState({});
  const [score, setScore] = useState(0);

  useEffect(() => {
    // Appel API pour récupérer les données de l'utilisateur
    const fetchData = async () => {
      const userData = await getUserData(userId);
      const activityData = await getUserActivity(userId);
      const averageSessionsData = await getUserAverageSessions(userId);
      const performanceData = await getUserPerformance(userId);

      setUserData(userData);
      setUserActivity(activityData);
      setUserAverageSessions(averageSessionsData);
      setUserPerformance(performanceData);
      // Supposons que 'score' soit directement dans userData
      setScore(userData.score || userData.todayScore);
    };

    fetchData();
  }, [userId]);

  return (
    <div className="App">
      <Topbar />
      <Sidebar />
      <div className='main-tool'>
        <div className='main-graph'>
          <UserProfile userId="12" />
          <DailyActivityChart data={userActivity} />
          <AverageSessionsChart data={userAverageSessions} />
          <PerformanceChart data={userPerformance} />
          <ScorePieChart score={score} />
        </div>
        <div className='main-data'>
          <KeyData data={userData} />
        </div>
      </div>
    </div>
  );
};

export default App;
