// src/components/PerformanceChart.js
import React, { useState, useEffect} from 'react';
import {Radar, RadarChart, PolarGrid, PolarAngleAxis, PolarRadiusAxis, ResponsiveContainer} from 'recharts';
import { getUserPerformance } from '../services/apiService';

const PerformanceChart = ({ userId }) => {
  const [performanceData, setPerformanceData] = useState([]);

  useEffect(() => {
    const fetchPerformanceData = async () => {
      const { data, kind } = await getUserPerformance(userId);
      if (data && kind) {
        const formattedData = data.map(item => ({
          subject: kind[item.kind], // Utiliser l'objet kind pour associer chaque performance à son label
          A: item.value,
          fullMark: 200
        }));
        setPerformanceData(formattedData);
      }
    };

    fetchPerformanceData();
  }, [userId]);

  return (
    <ResponsiveContainer className={'performance-charts-container'} width="100%" height="70%">
      <RadarChart cx="50%" cy="50%" outerRadius="80%" data={performanceData}>
        <PolarGrid />
        <PolarAngleAxis dataKey="subject" />
        <PolarRadiusAxis angle={30} domain={[0, 200]} />
        <Radar name="Performance" dataKey="A" stroke="#E60000" fill="#E60000" fillOpacity={0.6} />
      </RadarChart>
    </ResponsiveContainer>
  );
};

export default PerformanceChart;
