import React from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer } from 'recharts';

const ScorePieChart = ({ score }) => {
  // Convertir le score en pourcentage
  const scorePercentage = score * 100;
  const data = [
    { name: 'Score', value: scorePercentage },
    { name: 'Rest', value: 100 - scorePercentage },
  ];

  return (
    <ResponsiveContainer width="100%" height={500}>
      <PieChart>
        <Pie
          data={data}
          cx="50%"
          cy="50%"
          startAngle={90}
          endAngle={-270}
          innerRadius="80%"
          outerRadius="100%"
          fill="#8884d8"
          paddingAngle={0}
          dataKey="value"
        >
          {data.map((entry, index) => (
            <Cell key={`cell-${index}`} fill={index === 0 ? '#FF0000' : '#FFF'} />
          ))}
        </Pie>
      </PieChart>
    </ResponsiveContainer>
  );
};

export default ScorePieChart;
