// src/components/ScorePieChart.js
import React from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer, Label } from 'recharts';

const ScorePieChart = ({ score }) => {
  // Convertir le score en pourcentage
  const scorePercentage = score * 100;
  const data = [
    { name: 'Score', value: scorePercentage, fill: '#FF0000' },
    // Le reste du cercle sera transparent
    { name: 'Rest', value: 100 - scorePercentage, fill: 'transparent' },
  ];

  return (
    <ResponsiveContainer width="100%" height="100%">
      <PieChart>
        <Pie
          data={data}
          cx="50%"
          cy="50%"
          startAngle={90}
          endAngle={270} // Inverser le sens de remplissage
          innerRadius="60%"
          outerRadius="80%"
          paddingAngle={5}
          dataKey="value"
          cornerRadius={10}
          blendStroke
        >
          {data.map((entry, index) => (
            <Cell key={`cell-${index}`} fill={entry.fill} />
          ))}
<Label 
            value={`${scorePercentage}% de votre objectif`}
            position="center"
            className="chart-label"
            style={{
              fill: '#3a3a3a',
              fontSize: '20px',
              fontFamily: 'Arial'
            }}
          />
        </Pie>
      </PieChart>
    </ResponsiveContainer>
  );
};

export default ScorePieChart;
