// src/components/AverageSessionsChart.js
import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, ResponsiveContainer, Tooltip } from 'recharts';
import CustomTooltip from './CustomTooltip';
import { getUserAverageSession } from '../services/apiService';

const daysOfWeek = ['L', 'M', 'M', 'J', 'V', 'S', 'D']; // Labels pour les jours de la semaine

const AverageSessionsChart = ({ userId }) => {
  const [formattedData, setAverageSessionsData] = useState(null);

  useEffect(() => {
    const fetchAverageSessionsData = async () => {
      const data = await getUserAverageSession(userId);
      if (data && data.sessions) {
        // Transformer les données ici si nécessaire
        const formattedData = data.sessions.map(session => ({
          ...session,
          day: daysOfWeek[session.day - 1],
        }));
        setAverageSessionsData(formattedData);
      }
    };

    if (userId) {
      fetchAverageSessionsData();
    }
  }, [userId]);

  return (
    <ResponsiveContainer width="100%" height={300}>
      <LineChart data={formattedData}>
        <XAxis dataKey="day" />
        <Line type="monotone" dataKey="sessionLength" stroke="#FF0000" strokeWidth={2} dot={false} />
        <Tooltip content={<CustomTooltip type="averageSessions"/>} />
      </LineChart>
    </ResponsiveContainer>
  );
};

export default AverageSessionsChart;
