import React, { useState, useEffect } from 'react';
import { getUserData } from '../services/apiService';
// Import des icônes - ajustez le chemin selon votre structure de dossiers
import caloriesIcon from '../assets/icons/calories-icon.png';
import proteinIcon from '../assets/icons/protein-icon.png';
import carbsIcon from '../assets/icons/carbs-icon.png';
import fatIcon from '../assets/icons/fat-icon.png';

// Composant pour les informations clés
const KeyData = ({ userId }) => {
  const [keyData, setKeyData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
        const response = await getUserData(userId);
        // Assurez-vous d'accéder à la propriété `keyData` de l'objet `data` correctement
        if (response && response.data && response.data.keyData) {
            // pour déboguer
            console.log("Response Key Data:", response.data.keyData);
            setKeyData(response.data.keyData);
        }
    };

    fetchData();
}, [userId]);


  // Gestion de l'affichage lors du chargement des données
  if (!keyData) return <div>Chargement des données clés...</div>;

  // Destructurer les données clés pour un accès facile
  const { calorieCount, proteinCount, carbohydrateCount, lipidCount } = keyData;

  return (
    <div className="key-data-container">
      <div className="key-data-item">
        <img src={caloriesIcon} alt="Calories" />
        <p>{calorieCount}kCal</p>
        <p>Calories</p>
      </div>
      <div className="key-data-item">
        <img src={proteinIcon} alt="Proteins" />
        <p>{proteinCount}g</p>
        <p>Proteines</p>
      </div>
      <div className="key-data-item">
        <img src={carbsIcon} alt="Carbs" />
        <p>{carbohydrateCount}g</p>
        <p>Glucides</p>
      </div>
      <div className="key-data-item">
        <img src={fatIcon} alt="Fat" />
        <p>{lipidCount}g</p>
        <p>Lipides</p>
      </div>
    </div>
  );
};

export default KeyData;
