import React, {useState, useEffect} from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, ResponsiveContainer, Tooltip, Legend } from 'recharts';
import CustomTooltip from './CustomTooltip';
import { getUserActivity } from '../services/apiService';

// 'userId' est passé en prop au composant pour récupérer les données spécifiques à cet utilisateur
const DailyActivityChart = ({ userId }) => {
  const [activityData, setActivityData] = useState([]);

  useEffect(() => {
      const fetchActivityData = async () => {
          try {
              // Passer userId à getUserActivity
              const { data } = await getUserActivity(userId);
              if (data.sessions) {
                  setActivityData(data.sessions);
              }
          } catch (error) {
              console.error("Erreur lors de la récupération des données d'activité", error);
          }
      };

      fetchActivityData();
  }, [userId]); // Ajouter userId comme dépendance de useEffect pour relancer la requête si userId change

  // Transformation des données ici si nécessaire
  const formattedData = activityData.map(session => ({
      ...session,
      day: `${new Date(session.day).getDate()}`,
  }));
  
    return (
      <div className="daily-activity-chart">
        <div className="chart-header">
          <h2>Activité quotidienne</h2>
          <Legend verticalAlign="top" align="right" />
        </div>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={formattedData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="day" />
            <YAxis yAxisId="left" orientation="left" domain={['dataMin - 1', 'dataMax + 1']} />
            <YAxis yAxisId="right" orientation="right" domain={['dataMin - 50', 'dataMax + 50']} />
            <Bar yAxisId="left" dataKey="kilogram" fill="#5550bd" name="Poids (kg)" />
            <Bar yAxisId="right" dataKey="calories" fill="#ff0000" name="Calories brûlées" />
            <Tooltip content={<CustomTooltip type="dailyActivity"/>} className="daily-tooltip" cursor={{fill: 'transparent'}} />
          </BarChart>
        </ResponsiveContainer>
        </div>
    );
  };
  
  export default DailyActivityChart;
