import React, { useState, useEffect } from 'react';
import { getUserData } from '../services/apiService';

const UserProfile = ({ userId }) => {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const data = await getUserData(userId);
      setUserData(data); // Structure 'data' correspond à ce que l'API renvoie
    };

    fetchData();
  }, [userId]);

  if (!userData) return <div>Loading...</div>;
  // 'userData.data.userInfos' réponse de l'API enveloppée dans un objet 'data'
  if (!userData.data || !userData.data.userInfos) return <div>Données utilisateur non disponibles</div>;

  return (
    <div>
      <h1>Profil de l'utilisateur</h1>
      {/* Accès à la propriété 'firstName' */}
      <p>Prénom: {userData.data.userInfos.firstName}</p>
      {/* Ajouter plus de détails utilisateur ici */}
    </div>
  );
};

export default UserProfile;
