// src/components/ScorePieChart.js
import React from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer, Text } from 'recharts';

const ScorePieChart = ({ score }) => {
  // Convertir le score en pourcentage
  const scorePercentage = score * 100;
  const data = [
    { name: 'Score', value: scorePercentage, fill: '#FF0000' },
    { name: 'Rest', value: 100 - scorePercentage, fill: 'transparent' },
  ];

  return (
    <ResponsiveContainer width="100%" height="100%">
      <PieChart>
        <Pie
          data={data}
          cx="50%"
          cy="50%"
          startAngle={270}
          endAngle={630}
          innerRadius="70%"
          outerRadius="90%"
          paddingAngle={5}
          dataKey="value"
          cornerRadius={20}
          blendStroke
        >
          {data.map((entry, index) => (
            <Cell key={`cell-${index}`} fill={entry.fill} />
          ))}
        </Pie>
        {/* Ajout du texte au centre du cercle */}
        <text x="50%" y="50%" dy={8} textAnchor="middle" fill="#3a3a3a">
          {`${scorePercentage}%`}
          <tspan x="50%" y="50%" dy={20} textAnchor="middle" fill="#3a3a3a">
            de votre objectif
          </tspan>
        </text>
      </PieChart>
    </ResponsiveContainer>
  );
};

export default ScorePieChart;
