// src/components/ScorePieChart.js
import React from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer, Text } from 'recharts';

const ScorePieChart = ({ score }) => {
  // Convertir le score en pourcentage
  const scorePercentage = score * 100;
  const data = [
    { name: 'Score', value: scorePercentage, fill: '#FF0000' },
    // Le reste du cercle sera transparent
    { name: 'Rest', value: 100 - scorePercentage, fill: 'transparent' },
  ];

  return (
    <ResponsiveContainer width="100%" height={250}>
      <PieChart>
        <Pie
            data={data}
            cx="50%"
            cy="50%"
            startAngle={90}
            endAngle={270} // Inverser le sens de remplissage
            innerRadius="60%"
            outerRadius="80%"
            paddingAngle={5}
            dataKey="value"
            cornerRadius={10}
            blendStroke
        >
          {data.map((entry, index) => (
            <Cell key={`cell-${index}`} fill={entry.fill} />
          ))}
        </Pie>
        <Text 
          x="50%" 
          y="50%" 
          textAnchor="middle" 
          dominantBaseline="central" 
          className="chart-number"
          style={{ fontSize: '24px', fontWeight: 'bold' }}
        >
          {`${scorePercentage}%`}
        </Text>
        <Text 
          x="50%" 
          y="65%" 
          textAnchor="middle" 
          dominantBaseline="central" 
          className="chart-label"
          style={{ fontSize: '14px' }}
        >
          de votre objectif
        </Text>
      </PieChart>
    </ResponsiveContainer>
  );
};

export default ScorePieChart;
