// src/components/AverageSessionsChart.js
import React from 'react';
import { LineChart, Line, XAxis, ResponsiveContainer } from 'recharts';

const daysOfWeek = ['L', 'M', 'M', 'J', 'V', 'S', 'D']; // Labels pour les jours de la semaine

const AverageSessionsChart = ({ data }) => {
  // Transformer les données pour inclure les labels des jours de la semaine
  const formattedData = data.sessions.map(session => ({
    ...session,
    day: daysOfWeek[session.day - 1], // Utilisez 'day - 1' car les jours dans vos données commencent à 1 et non à 0
  }));

  return (
    <ResponsiveContainer width="100%" height={300}>
      <LineChart data={formattedData}>
        <XAxis dataKey="day" />
        <Line type="monotone" dataKey="sessionLength" stroke="#FF0000" strokeWidth={2} dot={false} />
        <Tooltip content={<CustomTooltip type="averageSessions"/>} />
      </LineChart>
    </ResponsiveContainer>
  );
};

export default AverageSessionsChart;
