import React, { useEffect, useState} from 'react';
import UserProfile from './components/UserProfile';
import DailyActivityChart from './components/DailyActivityChart';
import AverageSessionsChart from './components/AverageSessionsChart';
import PerformanceChart from './components/PerformanceChart';
import ScorePieChart from './components/ScorePieChart';
import { USER_AVERAGE_SESSIONS, USER_PERFORMANCE, USER_MAIN_DATA } from './data';
import KeyData from './components/KeyData';
import './App.css';
import Sidebar from './components/Sidebar';
import Topbar from './components/Topbar';
import { getUserActivity } from './services/apiService';

const App = () => {
  // Définition des états pour les données d'activité et pour gérer le chargement
  const [activityData, setActivityData] = useState(null);

  // Utilisation de useEffect pour charger les données d'activité dès le chargement du composant
  useEffect(() => {
    const fetchActivityData = async () => {
      const data = await getUserActivity(); // Remplacer par l'ID de l'utilisateur actuel
      setActivityData(data);
    };

    fetchActivityData();
  }, []);

  // Mock des données pour les autres composants
  const userAverageSessions = USER_AVERAGE_SESSIONS.find(session => session.userId === 12);
  const userPerformance = USER_PERFORMANCE.find(perf => perf.userId === 12);
  const userInfo = USER_MAIN_DATA.find(user => user.id === 12);
  const score = userInfo.todayScore || userInfo.score;

  return (
    <div className="App">
      <Topbar />
      <Sidebar />
      <div className='main-tool'>
        <div className='main-graph'>
          <UserProfile userId="12" />
          <DailyActivityChart data={activityData} />
          <AverageSessionsChart data={userAverageSessions} />
          <PerformanceChart data={userPerformance} />
          <ScorePieChart score={score} />
        </div>
        <div className='main-data'>
          <KeyData data={userInfo} />
        </div>
      </div>
    </div>
  );
};

export default App;
