import React, { useState, useEffect } from 'react';
import { getUserData } from '../services/apiService';

const UserProfile = ({ userId }) => {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const data = await getUserData(userId);
      setUserData(data);
    };

    fetchData();
  }, [userId]); // Le tableau de dépendances avec userId assure que l'effet s'exécute à nouveau si l'userId change.

  if (!userData) return <div>Loading...</div>;

  return (
    <div>
      <h1>User Profile</h1>
      {/* Afficher les données utilisateur ici */}
      <p>First Name: {userData.userInfos.firstName}</p>
      {/* Ajouter plus de détails utilisateur ici */}
    </div>
  );
};

export default UserProfile;
