import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, ResponsiveContainer, Tooltip } from 'recharts';
import { getUserActivity } from '../services/apiService';
import CustomTooltip from './CustomTooltip';

const DailyActivityChart = ({ userId }) => {
  const [activityData, setActivityData] = useState(null); // Utilisez null comme valeur initiale

  useEffect(() => {
    const fetchActivityData = async () => {
      try {
        const data = await getUserActivity(userId);
        // Assurez-vous que la structure des données correspond à celle attendue par le composant
        const formattedData = data.sessions.map(session => ({
          ...session,
          day: new Date(session.day).getDate(), // Ajustez selon le format de votre date
        }));
        setActivityData(formattedData);
      } catch (error) {
        console.error("Erreur lors de la récupération des données d'activité", error);
      }
    };

    if (userId) { // Vérifiez que userId est défini
      fetchActivityData();
    }
  }, [userId]); // Relancez l'effet si userId change

  if (!activityData) {
    return <div>Chargement des données d'activité...</div>;
  }
    return (
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={activityData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="day" />
          <YAxis yAxisId="left" orientation="left" domain={['dataMin - 1', 'dataMax + 1']} />
          <YAxis yAxisId="right" orientation="right" domain={['dataMin - 50', 'dataMax + 50']} />
          <Bar yAxisId="left" dataKey="kilogram" fill="#5550bd" name="Poids (kg)" />
          <Bar yAxisId="right" dataKey="calories" fill="#ff0000" name="Calories brûlées" />
          <Tooltip content={<CustomTooltip type="dailyActivity"/>} className="daily-tooltip" cursor={{fill: 'transparent'}} />
        </BarChart>
      </ResponsiveContainer>
    );
  };
  
  export default DailyActivityChart;
