import React from 'react';
import UserProfile from './components/UserProfile';
import DailyActivityChart from './components/DailyActivityChart';
import AverageSessionsChart from './components/AverageSessionsChart';
import PerformanceChart from './components/PerformanceChart';
import ScorePieChart from './components/ScorePieChart';
import { USER_PERFORMANCE, USER_MAIN_DATA } from './data';
import KeyData from './components/KeyData';
import './App.css';
import Sidebar from './components/Sidebar';
import Topbar from './components/Topbar';


const App = () => {


  // Mock des données pour les autres composants
  const userPerformance = USER_PERFORMANCE.find(perf => perf.userId === 12);
  const userInfo = USER_MAIN_DATA.find(user => user.id === 12);
  const score = userInfo.todayScore || userInfo.score;

  return (
    <div className="App">
      <Topbar />
      <Sidebar />
      <div className='main-tool'>
        <div className='main-graph'>
          <UserProfile userId="12" />
          <DailyActivityChart userId='12' />
          <AverageSessionsChart userId='12' />
          <PerformanceChart data={userPerformance} />
          <ScorePieChart score={score} />
        </div>
        <div className='main-data'>
          <KeyData data={userInfo} />
        </div>
      </div>
    </div>
  );
};

export default App;
