// src/components/AverageSessionsChart.js
import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, ResponsiveContainer, Tooltip, ReferenceArea } from 'recharts';
import CustomTooltip from './CustomTooltip';
import { getUserAverageSession } from '../services/apiService';

const daysOfWeek = ['L', 'M', 'M', 'J', 'V', 'S', 'D'];

const AverageSessionsChart = ({ userId }) => {
  const [averageSessionsData, setAverageSessionsData] = useState([]);
  const [hoveredDayIndex, setHoveredDayIndex] = useState(-1);

  useEffect(() => {
    const fetchAverageSessionsData = async () => {
      const response = await getUserAverageSession(userId);
      if (response && response.data && response.data.sessions) {
        const formattedData = response.data.sessions.map(session => ({
          ...session,
          day: daysOfWeek[session.day - 1] // Assurez-vous que le jour commence par 1 dans vos données
        }));
        setAverageSessionsData(formattedData);
      }
    };

    fetchAverageSessionsData();
  }, [userId]);

  const handleMouseOver = (index) => {
    setHoveredDayIndex(index);
  };

  const handleMouseLeave = () => {
    setHoveredDayIndex(-1);
  };

  if (!averageSessionsData || averageSessionsData.length === 0) {
    return <div>Chargement des données de sessions moyennes...</div>;
  }

  return (
    <div className="average-session-chart-wrapper">
      <h2 className="average-session-title">Durée moyenne des sessions</h2>
      <ResponsiveContainer className={'average-container'} width='100%' height='75%'>
        <LineChart data={averageSessionsData}>
          <XAxis dataKey="day" tick={{ fill: '#FFF', fontSize: 14 }} tickLine={false} axisLine={false}/>
          <Line type="monotone" dataKey="sessionLength" stroke="#FFF" strokeWidth={2} dot={{ onMouseOver: (e) => handleMouseOver(e.index) }} />
          <Tooltip content={<CustomTooltip type="averageSessions"/>} />
          {hoveredDayIndex !== -1 && (
            <ReferenceArea x1={hoveredDayIndex + 1} x2={averageSessionsData.length} fill="black" fillOpacity={0.975} onMouseLeave={handleMouseLeave} />
          )}
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

export default AverageSessionsChart;
