// CustomTooltip.js
import React from 'react';

const CustomTooltip = ({ active, payload, label, type }) => {
  if (active && payload && payload.length) {
    return (
      <div className="custom-tooltip">
        {type === 'dailyActivity' && (
          <>
            <div className="label-daily" style={{ backgroundColor: '#E60000', color:'#FFF', fontSize:'14px', padding: '15px', margin:'25px'}}>
              <p className='label-weight' style={{paddingBottom: '25px'}}>{`${payload[0].value}kg`}</p>
              <p>{`${payload[1].value}Kcal`}</p>
            </div>
          </>
        )}
        {type === 'averageSessions' && (
          <>
            <div className="label-average" style={{ backgroundColor: '#FFF', color:'#000', fontSize:'10px', margin:'0 0 10px 10px', width: '50px', height: '30px', display: 'flex', justifyContent: 'center', alignItems: 'center'}}>
              <p>{`${payload[0].value} min`}</p>
            </div>
          </>
        )}
      </div>
    );
  }
  return null;
};

export default CustomTooltip;
