import axios from 'axios';
import { USER_MAIN_DATA, USER_ACTIVITY, USER_AVERAGE_SESSIONS, USER_PERFORMANCE } from '../data.js';

const API_URL = 'http://localhost:3000/user/';
let useMockData = false; // Variable pour contrôler l'utilisation des données mockées

// Exposez une méthode pour basculer l'utilisation des données mockées
export const toggleMockDataUsage = () => {
  useMockData = !useMockData;
};

// Fonction pour obtenir les données d'un utilisateur
export const getUserData = async (userId) => {
  if (useMockData) {
    const userData = USER_MAIN_DATA.find(user => user.id === userId);
    console.log('données mockées', { data: userData || {} })
    return { data: userData || {} };
  }
  try {
    const response = await axios.get(`${API_URL}${userId}`);
    console.log(response.data);
    return response.data;
  } catch (error) {
    // Gérer l'erreur ici
    console.error("Erreur lors de la récuparation des données de l'utilisateur!", error);
  }
};

// Fonction pour obtenir les données d'activité d'un utilisateur
export const getUserActivity = async (userId) => {
  if (useMockData) {
    const activity = USER_ACTIVITY.find(activity => activity.userId === userId);
    return { data: activity ? activity.sessions : [] };
  }
  try {
    // Utiliser userId pour construire l'URL
    const response = await axios.get(`${API_URL}${userId}/activity`);
    console.log(response.data);
    return response.data;
  } catch (error) {
    console.error("Erreur lors de la récupération des données d'activité", error);
  }
};

// Fonction pour obtenir les données de temps de session d'un utilisateur
export const getUserAverageSession = async (userId) => {
  if (useMockData) {
    const sessionData = USER_AVERAGE_SESSIONS.find(session => session.userId === userId);
    return { data: sessionData ? sessionData.sessions : [] };
  }
  try {
    // Utiliser userId pour construire l'URL
    const response = await axios.get(`${API_URL}${userId}/average-sessions`);
    console.log(response.data);
    return response.data;
  } catch (error) {
    console.error("Erreur lors de la récupération des données de temps de session", error);
  }
};

// Fonction pour obtenir les données de performances d'un utilisateur
export const getUserPerformance = async (userId) => {
  if (useMockData) {
    const performance = USER_PERFORMANCE.find(perf => perf.userId === userId);
    return { data: performance ? performance.data : [], kind: performance ? performance.kind : {} };
  }
  try {
    // Utiliser userId pour construire l'URL
    const response = await axios.get(`${API_URL}${userId}/performance`);
    console.log(response.data);
    return response.data.data;
  } catch (error) {
    console.error("Erreur lors de la récupération des données de performance", error);
  }
};
