import React, {useState, useEffect} from 'react';
import UserProfile from './components/UserProfile';
import DailyActivityChart from './components/DailyActivityChart';
import AverageSessionsChart from './components/AverageSessionsChart';
import PerformanceChart from './components/PerformanceChart';
import ScorePieChart from './components/ScorePieChart';
import KeyData from './components/KeyData';
import './App.css';
import Sidebar from './components/Sidebar';
import Topbar from './components/Topbar';
import { toggleMockDataUsage, useMockData } from './services/apiService.js';


const App = () => {
  const [userId, setUserId] = useState(12);  // Initialisation avec l'ID 12
  const [dataKey, setDataKey] = useState(0);  // Clé pour forcer la remontage des composants

  // Méthode pour changer d'utilisateur
  const handleUserChange = () => {
    setUserId(userId === 12 ? 18 : 12);
  };

  const handleToggleData = () => {
    toggleMockDataUsage();
    setDataKey(prevKey => prevKey + 1);  // Incrémente la clé pour forcer le rechargement
  };

  useEffect(() => {
    console.log('Utilisation des données mockées:', useMockData);
  }, [useMockData]);

  return (
    <div className="App" key={dataKey}>
      <Topbar />
      <Sidebar />
      <button className='switch-id-button' onClick={handleUserChange}>Changer d'utilisateur</button>
      <button onClick={handleToggleData}>Basculer les données</button>
      <div className='user-profil-contain'>
        <UserProfile userId={userId} />
      </div>
      <div className='main-tool'>
        <div className='main-graph'>
          <DailyActivityChart userId={userId} />
          <div className='litle-graph'>
            <AverageSessionsChart userId={userId} />
            <PerformanceChart userId={userId} />
            <ScorePieChart userId={userId} />
          </div>
        </div>
        <div className='main-data'>
          <KeyData userId={userId} />
        </div>
      </div>
    </div>
  );
};

export default App;
