import React from 'react';
import UserProfile from './components/UserProfile';
import DailyActivityChart from './components/DailyActivityChart';
import AverageSessionsChart from './components/AverageSessionsChart';
import PerformanceChart from './components/PerformanceChart';
import ScorePieChart from './components/ScorePieChart';
import { USER_ACTIVITY, USER_AVERAGE_SESSIONS, USER_PERFORMANCE, USER_MAIN_DATA } from './data';
import './App.css'

const App = () => {
  const userActivity = USER_ACTIVITY.find(activity => activity.userId === 12);
  const userAverageSessions = USER_AVERAGE_SESSIONS.find(session => session.userId === 12);
  const userPerformance = USER_PERFORMANCE.find(perf => perf.userId === 12);
  const userInfo = USER_MAIN_DATA.find(user => user.id === 12);
  const score = userInfo.todayScore || userInfo.score; // Utilisez 'todayScore' ou 'score' en fonction de la structure de votre objet

  return (
    <div className="App">
      <UserProfile userId="12" />
      <DailyActivityChart data={userActivity} />
      <AverageSessionsChart data={userAverageSessions} />
      <PerformanceChart data={userPerformance} />
      <ScorePieChart score={score} />
    </div>
  );
};

export default App;
