// src/components/AverageSessionsChart.js
import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, ResponsiveContainer, Tooltip } from 'recharts';
import CustomTooltip from './CustomTooltip';
import { getUserAverageSession } from '../services/apiService';

const AverageSessionsChart = ({ userId }) => {
  const [averageSessionsData, setAverageSessionsData] = useState([]);

  useEffect(() => {
    const fetchAverageSessionsData = async () => {
      const response = await getUserAverageSession(userId);
      if (response && response.data && response.data.sessions) {
        // Utilisation directe des numéros de jour sans transformation
        setAverageSessionsData(response.data.sessions);
      }
    };

    fetchAverageSessionsData();
  }, [userId]);

  if (!averageSessionsData || averageSessionsData.length === 0) {
    return <div>Chargement des données de sessions moyennes...</div>;
  }

  return (
    <ResponsiveContainer className={'average-container'} width={258} height={263} style={{background: '#E60000'}}>
      <LineChart data={averageSessionsData}>
        <XAxis dataKey="day" />
        <Line type="monotone" dataKey="sessionLength" stroke="#FFF" strokeWidth={2} dot={false} />
        <Tooltip content={<CustomTooltip type="averageSessions"/>} />
      </LineChart>
    </ResponsiveContainer>
  );
};

export default AverageSessionsChart;
