import React, { useState } from 'react';
import UserProfile from './components/UserProfile';
import DailyActivityChart from './components/DailyActivityChart';
import AverageSessionsChart from './components/AverageSessionsChart';
import PerformanceChart from './components/PerformanceChart';
import ScorePieChart from './components/ScorePieChart';
import KeyData from './components/KeyData';
import './App.css';
import Sidebar from './components/Sidebar';
import Topbar from './components/Topbar';

// Importer les données mockées pour simuler une réponse locale
import { USER_MAIN_DATA, USER_ACTIVITY, USER_AVERAGE_SESSIONS, USER_PERFORMANCE } from './data.js';

const App = () => {
  const [userId, setUserId] = useState(12); // Initialisation avec l'ID 12
  const [useMockData, setUseMockData] = useState(false); // Utiliser les données mockées par défaut

  // Méthode pour changer d'utilisateur
  const handleUserChange = () => {
    setUserId(userId === 12 ? 18 : 12);
  };

  // Méthode pour basculer entre les données mockées et celles du serveur
  const toggleDataSrc = () => {
    setUseMockData(!useMockData);
  };

  return (
    <div className="App">
      <Topbar />
      <Sidebar />
      <button className='switch-id-button' onClick={handleUserChange}>Changer d'utilisateur</button>
      <button className='toggle-data-src-button' onClick={toggleDataSrc}>
        {useMockData ? 'Utiliser les données du serveur' : 'Utiliser les données mockées'}
      </button>
      <div className='user-profil-contain'>
        <UserProfile userId={userId} useMockData={useMockData} />
      </div>
      <div className='main-tool'>
        <div className='main-graph'>
          <DailyActivityChart userId={userId} useMockData={useMockData} />
          <div className='litle-graph'>
            <AverageSessionsChart userId={userId} useMockData={useMockData} />
            <PerformanceChart userId={userId} useMockData={useMockData} />
            <ScorePieChart userId={userId} useMockData={useMockData} />
          </div>
        </div>
        <div className='main-data'>
          <KeyData userId={userId} useMockData={useMockData} />
        </div>
      </div>
    </div>
  );
};

export default App;
